  state = {
    text: ""
  };

  getReadme = () => {
     API.getRepos(props.name)
       .then(res => { this.setState({ text: res });
     console.log(res)})
       .catch(err => console.log(err));
   };  

  componentDidMount() {
     this.getReadme();
  }

  render () { 
  return (<div className="cardy center">
  <div className="content">
    <ul>
      <li>
        <strong>Name:</strong> {props.name}
