import React, { Component } from "react";
import '../../public/assets/css/style.css';
import API from "../../utils/API";
import RepoCard from "../FriendCard";

class Portfolio extends Component {
  state = {
    repos: [],
    number: 0
  };

   searchRepos = () => {
     API.getRepos()
       .then(res => { this.setState({ repos: res.data });
     console.log(res.data)})
       .catch(err => console.log(err));
   };  

   componentDidMount() {
     this.searchRepos();
   }

   handleYesFormSubmit = event => {
    event.preventDefault();
    let number = this.state.number;
    let newNumber = number + 1;

  };

  // handleNoFormSubmit = event => {
  //   event.preventDefault();
  //   this.searchDogs();
  // };

  render() { 
    return (<div>
       <div className="info" id="about">
      <h1>Search Through My Repositories!</h1>
      <p>
        <div className="center"> 
       {this.state.repos.map(repo => (
          <RepoCard
            key={repo.id}
            name={repo.name}
            description={repo.description}
            url={repo.url}
          />
        ))}
        </div>
      </p>

      <button onClick={this.handleForward} className="btn btn-primary">
          Forward
        </button>
        <button onClick={this.handleBackward} className="btn btn-danger">
          Backward
        </button>
        </div>
    </div>)
  }
}

export default Portfolio;
